import React, { useState, useEffect } from 'react';
import Router from 'next/router';



const Perfil = () =>  {
	const [nombre, setNombre] = useState('');
	const [apellido, setApellido] = useState('');
	const [email, setEmail] = useState('');

	function setState(data){
		setNombre(data['nombre']);
		setApellido(data['apellido']);
		setEmail(data['email']);
	}

	useEffect(()=>{
		fetch("http://localhost:88/welcome/user/", {
			credentials: 'include'
		})
		.then(res => res.json().then(data => setState(data)))
	})

    return( 
      	<div className="Perfil">
			
	      	<button type="button" onClick={() => Router.push('/reproductor')}>Reproductor</button>
			<button>Logout</button>
      		<div className="Perfil">
				<h1 className="tit">Configuracion</h1>
				<h2 className="tit">Usuario</h2>
				<h3 className="edit">Editar</h3>
				
				<button>Cancelar</button>
				<button>Guardar</button>
				<div className="Perfil2">
					<ul>
						<li>{nombre}</li>
						<li>{apellido}</li>
						<li>{email}</li>						
					</ul>
				</div>
      		</div>
      	</div>
    )
}

export default Perfil;
